// Generated by BUCKLESCRIPT, PLEASE EDIT WITH CARE
'use strict';

var Belt_Option = require("bs-platform/lib/js/belt_Option.js");
var Caml_option = require("bs-platform/lib/js/caml_option.js");

function parameterize(t) {
  return t.toLowerCase().replace(/[^0-9a-zA-Z]+/gi, "-").replace(/^-|-$/gmi, "");
}

function paramToId(param) {
  return Belt_Option.map(Belt_Option.map(Caml_option.null_to_opt(/^\d+/g.exec(param)), (function (prim) {
                    return prim;
                  })), (function (param) {
                return param.join("");
              }));
}

exports.parameterize = parameterize;
exports.paramToId = paramToId;
/* No side effect */
